import {Table, Form, ModalFooter } from 'react-bootstrap';
import {Component, React} from 'react';


class app extends Component{
  constructor(props) {
    super(props)
    this.state = {
    value: '-',
    data:[]
    }
    
  }
  handlePeriodChange(selVal) {
    alert(' '+selVal);
    this.setState({value:selVal})
   
}

rows(){
  const rows=[]
    
    for(var i=0;i<20;i++){ 
    rows.push([]);
}
this.setState({ data: rows})
}
render(){
 
return(
    <>
    
    <div style={{"margin-top": 50,"margin-bottom": 50}} className="container"> 
    
    <Table striped bordered hover>
    <thead>
      <tr>
        <th>Course</th>
        <th>Credit Weight</th>
        <th>Grade</th>
        <th>Grade Point Value</th>
      </tr>
    </thead>
    <tbody>
    {this.state.data.map((n) => {
                    return (
                      <tr>
                      <td> <Form.Control type="text" /></td>
                      <td><Form.Control as="select" size="sm" onChange={(val) => this.handlePeriodChange(val.target.value)} custom>
                        <option value="-">-</option>
                        <option value="1">1</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="6">6</option>
                      </Form.Control></td>
                      <td>
                        <Form.Control as="select" size="sm" custom>
                        <option select>-</option>  
                        <option>A</option>
                        <option>B</option>
                        <option>C</option>
                        <option>D</option>
                        <option>E</option>
                      </Form.Control></td>
                      <td><Form.Control className="text-center" value={this.state.value} readonly disabled/></td>
                      </tr>
                    );
                    })}
    

  </tbody>
  </Table>
    </div>   
    <ModalFooter style={{"padding": 20,"background-color": '#343a40',  }}></ModalFooter>
    </>    
    );
}}

export default app;
