import {Table, Form, ModalFooter } from 'react-bootstrap';
import {Component, React} from 'react';
import Row from './rows';

class app extends Component{
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({value: event.target.value});
  }
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }
render(){
return(
    <>
    
    <div style={{"margin-top": 50,"margin-bottom": 50}} className="container"> 
    <Table striped bordered hover>
    <thead>
      <tr>
        <th>Course</th>
        <th>Credit Weight</th>
        <th>Grade</th>
        <th>Grade Point Value</th>
      </tr>
    </thead>
    <tbody>
    <Row object={[<Form><tr>
    <td> <Form.Control type="text" /></td>
    <td><Form.Control as="select" size="sm" value={this.state.value} onChange= {this.handleChange} custom>
      <option select>-</option>  
      <option>1</option>
      <option>3</option>
      <option>4</option>
      <option>6</option>
    </Form.Control></td>
    <td>
      <Form.Control as="select" size="sm" custom>
      <option select>-</option>  
      <option>A</option>
      <option>B</option>
      <option>C</option>
      <option>D</option>
      <option>E</option>
    </Form.Control></td>
    <td><Form.Control type="number" readonly/></td>
    </tr></Form>]}/>
</tbody>
  </Table>
    </div>   
    <ModalFooter style={{"padding": 20,"background-color": '#343a40',  }}></ModalFooter>
    </>    
    );
}}

export default app;
