import {Table, Form, ModalFooter } from 'react-bootstrap';
import {Component, React} from 'react';
import Row from './rows';

class app extends Component{
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    
  }
  handleChange(event) {
    this.setState({value: event.target.value});
    alert('A name was submitted: ' + this.state.value);
  }
  
render(){
return(
    <>
    
    <div style={{"margin-top": 50,"margin-bottom": 50}} className="container"> 
    
    <Table striped bordered hover>
    <thead>
      <tr>
        <th>Course</th>
        <th>Credit Weight</th>
        <th>Grade</th>
        <th>Grade Point Value</th>
      </tr>
    </thead>
    <tbody>
    <Row object={[<tr>
    <td> <Form.Control type="text" /></td>
    <td><Form.Control as="select" size="sm" value={this.state.value} onChange= {this.handleChange} custom>
      <option value="-">-</option>
      <option value="1">1</option>
      <option value="3">3</option>
      <option value="4">4</option>
      <option value="6">6</option>
    </Form.Control></td>
    <td>
      <Form.Control as="select" size="sm" custom>
      <option select>-</option>  
      <option>A</option>
      <option>B</option>
      <option>C</option>
      <option>D</option>
      <option>E</option>
    </Form.Control></td>
    <td><Form.Control type="number" readonly/></td>
    </tr>]}/>
</tbody>
  </Table>
    </div>   
    <ModalFooter style={{"padding": 20,"background-color": '#343a40',  }}></ModalFooter>
    </>    
    );
}}

export default app;
